        <a href=\"{{ path('member_news') }}\">Return</a>
    {% endif %}

    {{ include('_news_delete_form.html.twig') }}

    <footer class=\"footer footer d-flex flex-wrap justify-content-between align-items-center py-3 my-4 border-top position-relative\">
